<?php

namespace app\entities\admin;

use Yii;
use yii\db\ActiveRecord;

/**
 * @property int id
 * @property int product_id
 * @property int lang_id
 * @property string title
 * @property string description
 * @property string properties
 * @property string benefits
 * @property string video
 * @property string main_components
 * @property string composition
 * @property string recommendations
 * @property string dosage
 * @property string storage
 * @property string issue
 * @property string created_at
 * @property string updated_at
 */
class ProductTranslate extends ActiveRecord
{
    /**
     * @inheritdoc
     */
    public static function tableName()
    {
        return '{{%products_translate}}';
    }

    /**
     * @inheritdoc
     */
    public function attributeLabels()
    {
        return [
            'id' => Yii::t('admin', 'ID'),
            'product_id' => Yii::t('admin', 'Product ID'),
            'lang_id' => Yii::t('admin', 'Lang ID'),
            'title' => Yii::t('admin', 'Заголовок'),
            'description' => Yii::t('admin', 'Описание'),
            'properties' => Yii::t('admin', 'Свойства'),
            'benefits' => Yii::t('admin', 'Преимущества'),
            'video' => Yii::t('admin', 'Видео'),
            'main_components' => Yii::t('admin', 'Основные компоненты'),
            'composition' => Yii::t('admin', 'Состав'),
            'recommendations' => Yii::t('admin', 'Рекомендации'),
            'dosage' => Yii::t('admin', 'Дозировка'),
            'storage' => Yii::t('admin', 'Хранение'),
            'issue' => Yii::t('admin', 'Форма выпуска'),
            'created_at' => Yii::t('admin', 'Created date'),
            'updated_at' => Yii::t('admin', 'Updated date'),
        ];
    }

    public function getProduct()
    {
        return $this->hasOne(Product::class, ['id' => 'product_id']);
    }

    public static function create(
        $product_id,
        $lang_id
    ): self
    {
        $model = new self();
        $model->product_id = $product_id;
        $model->lang_id = $lang_id;

        return $model;
    }

    public function edit(
        $title,
        $description,
        $properties,
        $benefits,
        $video,
        $main_components,
        $composition,
        $recommendations,
        $dosage,
        $storage,
        $issue
    )
    {
        $this->title = $title;
        $this->description = $description;
        $this->properties = $properties;
        $this->benefits = $benefits;
        $this->video = $video;
        $this->main_components = $main_components;
        $this->composition = $composition;
        $this->recommendations = $recommendations;
        $this->dosage = $dosage;
        $this->storage = $storage;
        $this->issue = $issue;
    }

    public function getDescription()
    {
        return nl2br($this->description);
    }

    public function getProperties()
    {
        if (empty($this->properties)) {
            return false;
        }

        $html = '<ul class="product-properties">';
        foreach (explode("\n", $this->properties) as $val) {
            $html .= '<li class="product-properties__item"><i></i>' . $val . '</li>';
        }
        $html .= '</ul>';

        return empty($html)
            ? false
            : $html;
    }

    public function getBenefits()
    {
        if (empty($this->benefits)) {
            return false;
        }

        $html = '<ul class="product-benefits">';
        foreach (explode("\n\r\n", $this->benefits) as $blocks) {
            $rows = explode("\n", $blocks);
            $html .= '<li class="product-benefits__item">';
            if (isset($rows[0])) {
                $html .= '<div class="product__subtitle">' . $rows[0] . '</div>';
            }
            if (isset($rows[1])) {
                $html .= '<div class="product__text">' . $rows[1] . '</div>';
            }
            $html .= '</li>';
        }
        $html .= '</ul>';

        return $html ;
    }

    public function getVideo()
    {
        if (empty($this->video)) {
            return false;
        }

        $src = $this->video;
        $lang = Yii::$app->language;

        // youtube.com
        $src = preg_replace(
            '#((https|http):\/\/([a-zA-Z0-9]+\.|)youtube\.com\/.*v=([a-zA-Z0-9_-]+).*)#i',
            'https://www.youtube.com/embed/\\4?cc_load_policy=1&mode=opaque&autohide=1&wmode=transparent&cc_lang_pref=' . $lang . '&showinfo=0&hl=' . $lang,
            $src
        );

        // youtu.be
        $src = preg_replace(
            '#((https|http):\/\/youtu\.be\/([a-zA-Z0-9_-]+).*)#i',
            'https://www.youtube.com/embed/\\3?cc_load_policy=1&mode=opaque&autohide=1&wmode=transparent&cc_lang_pref=' . $lang . '&showinfo=0&hl=' . $lang,
            $src
        );

        // vimeo.com
        $src = preg_replace(
            '#((https|http):\/\/([a-zA-Z0-9]+\.|)vimeo\.com.*[/=:]([0-9]+))#i',
            'https://player.vimeo.com/video/\\4?color=1b1c1f&title=0&byline=0&portrait=0&badge=0',
            $src
        );

        $html = '<div class="product-video">';
        $html .= '<iframe src="' . $src . '" width="800" height="450" class="product-video__player" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>';
        $html .= '</div>';

        return $html;
    }

