    public function getMainComponents()
    {
        if (empty($this->main_components))
            return false;

        $html = '';
        foreach (explode("\r\n\r\n", $this->main_components) as $key => $blocks) {
            $rows = explode("\r\n", $blocks);
            $html .= '<li class="product-main-components__item">';
            if (isset($rows[0])) {
                $html .= '<div class="product__subtitle"><i></i>' . $rows[0] . '</div>';
                if (isset($rows[1])) {
                    $html .= '<div class="product__text">';
                    foreach ($rows as $key => $val) {
                        if ($key !== 0) {
                            if ($key !== 1) {
                                $html .= '<br><br>';
                            }
                            $html .= $val;
                        }
                    }
                    $html .= '</div>';
                }
            }
            $html .= '</li>';
        }

        return $html;
    }

    public function getcomposition()
    {
        if (empty($this->composition))
            return false;

        $html = '';
        foreach (explode("\r\n\r\n", trim($this->composition)) as $key => $val) {
            if ($key === 0) {
                $html .= '<div class="product__text">' . $val . '</div>';
            } else {
                $html .= '<div class="product-composition">';
                foreach (explode("\n", trim($val)) as $tr) {
                    $td = explode(" - ", $tr);
                    $html .= '<div class="product-composition__row">';
                    if ($td[0])
                        $html .= '<div class="product-composition__text">' . $td[0] . '</div>';
                    if ($td[1])
                        $html .= '<div class="product-composition__value">' . $td[1] . '</div>';
                    $html .= '</div>';
                }
                $html .= '</div>';
            }
        }

        return $html;
    }

    public function getRecommendations()
    {
        if (empty($this->recommendations))
            return false;

        return nl2br($this->recommendations);
    }

    public function getDosage()
    {
        if (empty($this->dosage))
            return false;

        return nl2br($this->dosage);
    }

    public function getIssue()
    {
        if (empty($this->issue))
            return false;

        return $this->issue;
    }

    public function getStorage()
    {
        if (empty($this->storage))
            return false;

        return nl2br($this->storage);
    }
